import React from 'react';
import '../components/GroupChats.css'; // CSS file for styling

const ExploreGC = () => {
    const chats = [
      {
        name: 'Local Jazz Bars',
        members: 4,
        description: "Paris' jazz clubs are more than just music venues; they are a testament to the city's enduring love affair with jazz. Meet people to go to these clubs with, speak to them real-time about any questions.",
        location: 'Paris, France',
        roomCode: 'FPB',
      },
      {
        name: 'Markets',
        members: 7,
        description: "Santorini is known for its most gorgeous sunrises and sunsets with the view of endless ocean. Perfect place to plan a proposal or go for honeymoon. Local markets are authentic and beautiful. Explore Santorini like an insider.",
        location: 'Santorini, Greece',
        roomCode: 'FPB',
      },
      {
        name: 'Book Tours, Classes, Activities',
        members: 12,
        description: "Turkey's beautiful Cappadocia has much to offer, ranging from its pottery classes, historical views, early morning hot air balloon sky, wine tasting, caves viewing, etc. Book slots through this group.",
        location: 'Cappadocia, Turkey',
        roomCode: 'FPB',
      },
    ];

    return (
      <div className="group-chats-container">
        {/* Header */}
        <div className="header">Explore Group Chats</div>

        {/* Chat List */}
        <div className="chat-list">
          {chats.map((chat, index) => (
            <div key={index} className="chat-card">
              {/* Chat Name and Members */}
              <div className="chat-name">
                {chat.name}: {chat.members} Members
              </div>

              {/* Chat Description */}
              <div className="chat-description">{chat.description}</div>

              {/* Chat Footer */}
              <div className="chat-footer">
                <span>{chat.location}</span>
                <span>Room Code: {chat.roomCode}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default ExploreGC;
